INSERT INTO `recument` (`id`, `title`, `title_vi`, `id_recument_type`, `content`, `content_vi`, `status`) VALUES
(1, 'Developers', 'Lập trình viên', 1, '<p><strong>Job description:</strong><br />\r\nDevelop websites, software, and projects on NET base.<br />\r\nDesign custom webs.</p>\r\n\r\n<p><strong>Experience/skills in:</strong></p>\r\n\r\n<ul>\r\n	<li>ASP.NET Core 1.0</li>\r\n	<li>Entity Framework 6,7</li>\r\n	<li>Mysql</li>\r\n	<li>HTML, CSS, Bootstrap 3.*.</li>\r\n	<li>Jquery, Ajax</li>\r\n</ul>\r\n\r\n<p><strong>Benefits:</strong><br />\r\n- Working hours: 8h30-17h30, Monday to Friday (1hour/day for lunch)<br />\r\n- Free coffee, parking available<br />\r\n- Birthday party<br />\r\n- Friendly working environment<br />\r\n- Appreciate teamwork<br />\r\n- A chance to work in multinational team with great people<br />\r\n- Salary as applicant ability, renew each year</p>\r\n\r\n<p><strong>Contact</strong><br />\r\nEmail: quangthao1606@gmail.com or <a href="mailto:hrmanagement@hbbsolution.com">hrmanagement@hbbsolution.com</a></p>\r\n', '<p><strong>M&ocirc; tả c&ocirc;ng việc:</strong></p>\r\n\r\n<p>X&acirc;y dựng website cho kh&aacute;ch h&agrave;ng, phần mềm, dự &aacute;n dựa tr&ecirc;n nền tảng .NET framework.<br />\r\nThiết kế web dựa tr&ecirc;n nhu cầu của kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p><strong>Kỹ năng/kinh nghiệm:</strong></p>\r\n\r\n<ul>\r\n	<li>C&oacute; kinh nghiệm với ASP.NET Core 1.0</li>\r\n	<li>C&oacute; kinh nghiệm với Entity Framework 6,7</li>\r\n	<li>C&oacute; kinh nghiệm với Mysql</li>\r\n	<li>C&oacute; kinh nghiệm với HTML, CSS, Bootstrap 3.*.</li>\r\n	<li>C&oacute; kinh nghiệm với Jquery, Ajax</li>\r\n</ul>\r\n\r\n<p><strong>Quyền lợi:</strong><br />\r\n- L&agrave;m việc từ T2-T6 8h30-17h30<br />\r\n- Free coffe, gửi xe, party sinh nhật theo qu&yacute;<br />\r\n- M&ocirc;i trường l&agrave;m việc th&acirc;n thiện, trẻ nhiệt huyết<br />\r\n- Hưởng lương theo năng lực, review lương h&agrave;ng năm</p>\r\n\r\n<p><strong>Li&ecirc;n hệ:</strong><br />\r\nEmail: quangthao1606@gmail.com or hrmanagement@hbbsolution.com&nbsp;</p>\r\n', 1),
(2, 'Senior Dev', 'Lập trình viên', 2, '<p><strong>Job description</strong></p>\r\n\r\n<p>Attend to develop applications on Android/iOS<br />\r\nDevelop projects individually<br />\r\nWork and collaborate in team according to job division of team leader.</p>\r\n\r\n<p><strong>Experiences/skills in detail:</strong></p>\r\n\r\n<p><strong>1. Android</strong></p>\r\n\r\n<p>Use fluently Editor Eclipse, Android Studio.<br />\r\nStrong knowledge&nbsp;of handling&nbsp;layout, thread, service, broadcast receiver, custom views&hellip;.<br />\r\nHave experiences in using libraries and SDK.<br />\r\nWork independently, and team working.</p>\r\n\r\n<p><strong>2. iOS</strong></p>\r\n\r\n<p>Use fluently Swift, have knowledge Objective C&nbsp;is an advantage.<br />\r\nSolid knowledge about framework: UIKit, StoreKit, MessageUI,CFNetwork, CoreData.<br />\r\nHave experiences about Social Network in iOS such as Facebook, Twitter&hellip;<br />\r\nUse fluently SQLite, client-server in REST v&agrave; JSON.</p>\r\n\r\n<p><strong>Benefits:</strong><br />\r\n- Working hours: 8h30-17h30, Monday to Friday (1hour/day for lunch)<br />\r\n- Free coffee, parking available<br />\r\n- Birthday party<br />\r\n- Friendly working environment<br />\r\n- Appreciate teamwork<br />\r\n- A chance to work in multinational team with great people<br />\r\n- Salary as applicant ability, renew each year</p>\r\n\r\n<p><strong>Contact</strong><br />\r\nEmail: <u>quangthao1606@gmail.com</u>&nbsp;or <a href="mailto:hrmanagement@hbbsolution.com">hrmanagement@hbbsolution.com</a></p>\r\n', '<p><strong>M&ocirc; tả c&ocirc;ng việc:</strong></p>\r\n\r\n<p>X&acirc;y dựng c&aacute;c ứng dụng di động theo nhu cầu của kh&aacute;ch h&agrave;ng.<br />\r\nPh&aacute;t triển sản phẩm của c&ocirc;ng ty.<br />\r\nĐồng bộ dữ liệu với website.</p>\r\n\r\n<p><strong>Kỹ năng/kinh nghiệm:</strong></p>\r\n\r\n<p><strong>1. Android</strong></p>\r\n\r\n<p>Thường sử dụng Eclipse, Android Studio.<br />\r\nTh&agrave;nh thạo xử l&yacute; layout, thread, service, broadcast receiver, custom views&hellip;.<br />\r\nC&oacute; kinh nghiệm sử dụng c&aacute;c thư viện v&agrave; SDK hỗ trợ.<br />\r\nChủ động trong c&ocirc;ng việc, c&oacute; kỹ năng l&agrave;m việc nh&oacute;m.</p>\r\n\r\n<p><strong>2. iOS</strong></p>\r\n\r\n<p>Th&agrave;nh thạo Swift, từng l&agrave;m việc với Objective C&nbsp;l&agrave; một&nbsp;lợithế.<br />\r\nSử dụng th&agrave;nh thạo c&aacute;c framework: UIKit, StoreKit, MessageUI,CFNetwork, CoreData.<br />\r\nC&oacute; kinh nghiệm l&agrave;m về Social Network tr&ecirc;n iOS như Facebook, Twitter&hellip;<br />\r\nThao t&aacute;c tốt với SQLite, giao tiếp client-server th&ocirc;ng qua REST v&agrave; JSON.</p>\r\n\r\n<p><strong>Quyền lợi:</strong><br />\r\n- L&agrave;m việc từ T2-T6 8h30-17h30<br />\r\n- Free coffe, gửi xe, party sinh nhật theo qu&yacute;<br />\r\n- M&ocirc;i trường l&agrave;m việc th&acirc;n thiện, trẻ nhiệt huyết<br />\r\n- Hưởng lương theo năng lực, review lương h&agrave;ng năm</p>\r\n\r\n<p><strong>Li&ecirc;n hệ:</strong><br />\r\nEmail: quangthao1606@gmail.com or hrmanagement@hbbsolution.com&nbsp;</p>\r\n', 1),
(3, 'iOS ', 'iOS ', 3, '', '', 0),
(4, 'Server Admin position', 'Vị trí quản trị hệ thống server', 0, '<p>We are currently open for 01 position of Server Admin</p>\r\n\r\n<p><strong>1. Skills and experiment:</strong></p>\r\n\r\n<p>&nbsp; 1.1 At least 01 year working as:</p>\r\n\r\n<p style="margin-left:40px">+ Server administrator: Window server, Linux server, Web server (Apache, Ngixn,... - IIS), Mail server.<br />\r\n+ DBMS administrator: MSSQL,&nbsp;MySQL ...<br />\r\n+ System security (Window, Linux, Firewall).<br />\r\n+ Domain Name System (DNS).</p>\r\n\r\n<p>&nbsp;1.2 It is a plus if applicant has knowledge in:</p>\r\n\r\n<p style="margin-left:40px">+ Hosting control panel: Cpanel, Direct Admin, Plesk.</p>\r\n\r\n<p style="margin-left:40px">+ Programming languages such as <a href="https://l.facebook.com/l.php?u=http%3A%2F%2FASP.NET%2F&amp;h=ATPYQdxY-AAYwtiCk9uHp899cxkRxMoNA0zlG82mjTkQ42RLDL-RUG_gE17nMpJEF-PfxaG5pdCdwy_9DgJY2uWyBodk2P6t2HEmpuqliCqwxY7sjyEZk2CYxiFn-3rv8jFIrlm7TnMvBoDF7W3EUQ&amp;enc=AZMtC0L2C7o2asChEroDpCvyLrdvIsKCDPqdYpMmNllXzJMNJ7PQ_T-mCevAAjbcojnEa2gPUdT9uabkpgoo-bSWegZcXGpY12x7eNYWlODYCnX7JTzVg7w0ee94lnIHh2tut-Wpuh_NnvK2Iaq_CWysS_MLzCgLfZIz_V51zOn8GbxM-jCl90t4wHYGbBCabfQ&amp;s=1" onclick="LinkshimAsyncLink.referrer_log(this, &quot;http:\\/\\/ASP.NET\\/&quot;, &quot;\\/si\\/ajax\\/l\\/render_linkshim_log\\/?u=http\\u00253A\\u00252F\\u00252FASP.NET\\u00252F&amp;h=ATPYQdxY-AAYwtiCk9uHp899cxkRxMoNA0zlG82mjTkQ42RLDL-RUG_gE17nMpJEF-PfxaG5pdCdwy_9DgJY2uWyBodk2P6t2HEmpuqliCqwxY7sjyEZk2CYxiFn-3rv8jFIrlm7TnMvBoDF7W3EUQ&amp;enc=AZMtC0L2C7o2asChEroDpCvyLrdvIsKCDPqdYpMmNllXzJMNJ7PQ_T-mCevAAjbcojnEa2gPUdT9uabkpgoo-bSWegZcXGpY12x7eNYWlODYCnX7JTzVg7w0ee94lnIHh2tut-Wpuh_NnvK2Iaq_CWysS_MLzCgLfZIz_V51zOn8GbxM-jCl90t4wHYGbBCabfQ&amp;d&quot;);" rel="nofollow noopener" target="_blank">ASP.NET</a>, PHP.</p>\r\n\r\n<p style="margin-left:40px">+ Keen on technical solutions of security and system administration</p>\r\n\r\n<p style="margin-left:40px">+ Team working and independent working</p>\r\n\r\n<p style="margin-left:40px">+ Ability of research/solve new problems</p>\r\n\r\n<p style="margin-left:40px">+ Be responsible and carefull</p>\r\n\r\n<p><strong>2. Degree and certificates:</strong><br />\r\n- Bachelor degree with major in&nbsp;Computer Network/Software Engeneering<br />\r\n- MCSA, MCSE, LPI and&nbsp;CCNA certificate.<br />\r\n- Good communicate in English.</p>\r\n\r\n<p><strong>3. Benefits:</strong><br />\r\n- Working hours: 8h30-17h30,&nbsp;Monday to Friday (1hour/day for lunch)<br />\r\n- Never OT.<br />\r\n- Free coffe, bike parking</p>\r\n\r\n<p>- Birthday party .<br />\r\n- Friendly, motivative working environment.<br />\r\n- Salary as applicant ability, renew each year.</p>\r\n\r\n<p><strong>CONTACT:</strong><br />\r\nEmail: quangthao1606@gmail.com or hrmanagement@hbbsolution.com</p>\r\n', '<p>Hiện tại HBB Solutions đang c&oacute; nhu cầu tuyển một QUẢN TRỊ HỆ THỐNG SERVER.&nbsp;</p>\r\n\r\n<p><strong>1. Y&ecirc;u cầu kỹ năng:</strong></p>\r\n\r\n<p>&nbsp; 1.1 C&oacute; kinh nghiệm &iacute;t nhất 1 năm trở l&ecirc;n về c&aacute;c lĩnh vực:</p>\r\n\r\n<p style="margin-left:40px">+ Quản trị hệ thống server Window, Linux, Web server (Apache, Ngixn,... - IIS), Mail server.<br />\r\n+ Quản trị hệ thống cơ sở dữ liệu MSSQL v&agrave; MySQL.<br />\r\n+ Bảo mật hệ thống (Window, Linux, Firewall).<br />\r\n+ Nắm vững kiến thức Domain Name System (DNS).</p>\r\n\r\n<p>1. 2 C&oacute; th&ecirc;m kiến thức v&agrave; kinh nghiệm sau đ&acirc;y l&agrave; một lợi thế:</p>\r\n\r\n<p style="margin-left:40px">+ Đ&atilde; từng sử dụng c&aacute;c Hosting control panel: Cpanel, Direct Admin, Plesk.<br />\r\n+ Đ&atilde; từng l&agrave;m qua v&agrave; hiểu biết c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh như&nbsp;<a href="https://l.facebook.com/l.php?u=http%3A%2F%2FASP.NET%2F&amp;h=ATPYQdxY-AAYwtiCk9uHp899cxkRxMoNA0zlG82mjTkQ42RLDL-RUG_gE17nMpJEF-PfxaG5pdCdwy_9DgJY2uWyBodk2P6t2HEmpuqliCqwxY7sjyEZk2CYxiFn-3rv8jFIrlm7TnMvBoDF7W3EUQ&amp;enc=AZMtC0L2C7o2asChEroDpCvyLrdvIsKCDPqdYpMmNllXzJMNJ7PQ_T-mCevAAjbcojnEa2gPUdT9uabkpgoo-bSWegZcXGpY12x7eNYWlODYCnX7JTzVg7w0ee94lnIHh2tut-Wpuh_NnvK2Iaq_CWysS_MLzCgLfZIz_V51zOn8GbxM-jCl90t4wHYGbBCabfQ&amp;s=1" onclick="LinkshimAsyncLink.referrer_log(this, &quot;http:\\/\\/ASP.NET\\/&quot;, &quot;\\/si\\/ajax\\/l\\/render_linkshim_log\\/?u=http\\u00253A\\u00252F\\u00252FASP.NET\\u00252F&amp;h=ATPYQdxY-AAYwtiCk9uHp899cxkRxMoNA0zlG82mjTkQ42RLDL-RUG_gE17nMpJEF-PfxaG5pdCdwy_9DgJY2uWyBodk2P6t2HEmpuqliCqwxY7sjyEZk2CYxiFn-3rv8jFIrlm7TnMvBoDF7W3EUQ&amp;enc=AZMtC0L2C7o2asChEroDpCvyLrdvIsKCDPqdYpMmNllXzJMNJ7PQ_T-mCevAAjbcojnEa2gPUdT9uabkpgoo-bSWegZcXGpY12x7eNYWlODYCnX7JTzVg7w0ee94lnIHh2tut-Wpuh_NnvK2Iaq_CWysS_MLzCgLfZIz_V51zOn8GbxM-jCl90t4wHYGbBCabfQ&amp;d&quot;);" rel="nofollow noopener" target="_blank">ASP.NET</a>, PHP.<br />\r\n+ H&ograve;a đồng, c&oacute; khả năng l&agrave;m việc nh&oacute;m hoặc l&agrave;m việc độc lập, c&oacute; khả năng nghi&ecirc;n cứu ph&aacute;t triển.<br />\r\n+ Đam m&ecirc; c&ocirc;ng việc hỗ trợ kỹ thuật - bảo mật v&agrave; quản trị hệ thống, l&agrave;m việc cẩn thận, c&oacute; tinh thần tr&aacute;ch nhiệm.</p>\r\n\r\n<p><strong>2. Y&ecirc;u cầu tr&igrave;nh độ:</strong><br />\r\n- Tốt nghiệp Cao Đẳng, Đại Học Chuy&ecirc;n ng&agrave;nh C&ocirc;ng Nghệ Th&ocirc;ng Tin.<br />\r\n- Đ&atilde; đạt một trong c&aacute;c chứng chỉ l&agrave; một lợi thế: MCSA, MCSE, LPI v&agrave; CCNA.<br />\r\n- Anh văn tr&igrave;nh độ B hoặc tương đương, c&oacute; khả năng đọc hiểu tốt.</p>\r\n\r\n<p><strong>3. Quyền lợi:</strong><br />\r\n- L&agrave;m việc từ T2-T6 8h30-17h30<br />\r\n- Never OT.<br />\r\n- Free coffe, gửi xe, party sinh nhật theo qu&yacute;.<br />\r\n- M&ocirc;i trường l&agrave;m việc th&acirc;n thiện, trẻ nhiệt huyết.<br />\r\n- Hưởng lương theo năng lực, review lương h&agrave;ng năm.</p>\r\n\r\n<p><strong>TH&Ocirc;NG TIN LI&Ecirc;N HỆ:</strong><br />\r\nEmail: quangthao1606@gmail.com or hrmanagement@hbbsolution.com</p>\r\n', 1),
(5, 'Server Admin', 'Quản trị viên', 4, '<p>We are currently open for 01 position of Server Admin</p>\r\n\r\n<p><strong>1. Skills and experiment:</strong><br />\r\n1.1 At least 01 year working as:<br />\r\n&nbsp;&nbsp;&nbsp;+ Server administrator: Window server, Linux server, Web server (Apache, Ngixn,... - IIS), Mail server.<br />\r\n&nbsp;&nbsp;&nbsp;+ DBMS administrator: MSSQL, MySQL ...<br />\r\n&nbsp;&nbsp;&nbsp;+ System security (Window, Linux, Firewall).<br />\r\n&nbsp;&nbsp;&nbsp;+ Domain Name System (DNS).<br />\r\n1.2 It is a plus if applicant has knowledge in:<br />\r\n&nbsp;&nbsp;&nbsp;+ Hosting control panel: Cpanel, Direct Admin, Plesk.<br />\r\n&nbsp;&nbsp;&nbsp;+ Programming languages such as <a href="https://l.facebook.com/l.php?u=http%3A%2F%2FASP.NET%2F&amp;h=ATPYQdxY-AAYwtiCk9uHp899cxkRxMoNA0zlG82mjTkQ42RLDL-RUG_gE17nMpJEF-PfxaG5pdCdwy_9DgJY2uWyBodk2P6t2HEmpuqliCqwxY7sjyEZk2CYxiFn-3rv8jFIrlm7TnMvBoDF7W3EUQ&amp;enc=AZMtC0L2C7o2asChEroDpCvyLrdvIsKCDPqdYpMmNllXzJMNJ7PQ_T-mCevAAjbcojnEa2gPUdT9uabkpgoo-bSWegZcXGpY12x7eNYWlODYCnX7JTzVg7w0ee94lnIHh2tut-Wpuh_NnvK2Iaq_CWysS_MLzCgLfZIz_V51zOn8GbxM-jCl90t4wHYGbBCabfQ&amp;s=1" onclick="LinkshimAsyncLink.referrer_log(this, ">ASP.NET</a>, PHP.<br />\r\n&nbsp;&nbsp;&nbsp;+ Keen on technical solutions of security and system admin<br />\r\n&nbsp;&nbsp;&nbsp;+ Enjoy learning new techniques<br />\r\n&nbsp;&nbsp;&nbsp;+ Always think about improving system<br />\r\n&nbsp;&nbsp;&nbsp;+ Team working and independent working<br />\r\n&nbsp;&nbsp;&nbsp;+ Ability of research/solve new problems<br />\r\n&nbsp;&nbsp;&nbsp;+ Be responsible and careful</p>\r\n\r\n<p><strong>2. Degree and certificates:</strong><br />\r\n- Bachelor or higher degree with major in Computer Network/Software Engineering or equivalent<br />\r\n- MCSA, MCSE, LPI, CCNA certificate is a must<br />\r\n- Good oral and written communication skills in English (another language is a plus)</p>\r\n\r\n<p><strong>3. Benefits:</strong><br />\r\n- Working hours: 8h30-17h30, Monday to Friday (1hour/day for lunch)<br />\r\n- Free coffee, parking available<br />\r\n- Birthday party<br />\r\n- Friendly working environment<br />\r\n- Appreciate teamwork<br />\r\n- A chance to work in multinational team with great people<br />\r\n- Salary as applicant ability, renew each year</p>\r\n\r\n<p><strong>Contact:</strong><br />\r\nEmail: quangthao1606@gmail.com or <a href="mailto:hrmanagement@hbbsolution.com">hrmanagement@hbbsolution.com</a></p>\r\n', '<p>Hiện tại HBB Solutions đang c&oacute; nhu cầu tuyển một QUẢN TRỊ HỆ THỐNG SERVER.<br />\r\n<strong>1. Y&ecirc;u cầu kỹ năng:</strong><br />\r\n1.1 C&oacute; kinh nghiệm &iacute;t nhất 1 năm trở l&ecirc;n về c&aacute;c lĩnh vực:<br />\r\n&nbsp;+ Quản trị hệ thống server Window, Linux, Web server (Apache, Ngixn,... - IIS), Mail server<br />\r\n&nbsp;+ Quản trị hệ thống cơ sở dữ liệu MSSQL v&agrave; MySQL<br />\r\n&nbsp;+ Bảo mật hệ thống (Window, Linux, Firewall)<br />\r\n&nbsp;+ Nắm vững kiến thức Domain Name System (DNS)<br />\r\n1. 2 C&oacute; th&ecirc;m kiến thức v&agrave; kinh nghiệm sau đ&acirc;y l&agrave; một lợi thế:<br />\r\n&nbsp;+ Đ&atilde; từng sử dụng c&aacute;c Hosting control panel: Cpanel, Direct Admin, Plesk<br />\r\n&nbsp;+ Đ&atilde; từng l&agrave;m qua v&agrave; hiểu biết c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh như <a href="https://l.facebook.com/l.php?u=http%3A%2F%2FASP.NET%2F&amp;h=ATPYQdxY-AAYwtiCk9uHp899cxkRxMoNA0zlG82mjTkQ42RLDL-RUG_gE17nMpJEF-PfxaG5pdCdwy_9DgJY2uWyBodk2P6t2HEmpuqliCqwxY7sjyEZk2CYxiFn-3rv8jFIrlm7TnMvBoDF7W3EUQ&amp;enc=AZMtC0L2C7o2asChEroDpCvyLrdvIsKCDPqdYpMmNllXzJMNJ7PQ_T-mCevAAjbcojnEa2gPUdT9uabkpgoo-bSWegZcXGpY12x7eNYWlODYCnX7JTzVg7w0ee94lnIHh2tut-Wpuh_NnvK2Iaq_CWysS_MLzCgLfZIz_V51zOn8GbxM-jCl90t4wHYGbBCabfQ&amp;s=1" onclick="LinkshimAsyncLink.referrer_log(this, ">ASP.NET</a>, PHP<br />\r\n&nbsp;+ H&ograve;a đồng, c&oacute; khả năng l&agrave;m việc nh&oacute;m hoặc l&agrave;m việc độc lập, c&oacute; khả năng nghi&ecirc;n cứu ph&aacute;t triển<br />\r\n&nbsp;+ Đam m&ecirc; c&ocirc;ng việc hỗ trợ kỹ thuật - bảo mật v&agrave; quản trị hệ thống<br />\r\n&nbsp;+ L&agrave;m việc cẩn thận, c&oacute; tinh thần tr&aacute;ch nhiệm<br />\r\n<strong>2. Y&ecirc;u cầu tr&igrave;nh độ:</strong><br />\r\n- Tốt nghiệp Cao Đẳng, Đại Học Chuy&ecirc;n ng&agrave;nh C&ocirc;ng Nghệ Th&ocirc;ng Tin<br />\r\n- Đ&atilde; đạt một trong c&aacute;c chứng chỉ l&agrave; một lợi thế: MCSA, MCSE, LPI v&agrave; CCNA<br />\r\n- Anh văn tr&igrave;nh độ B hoặc tương đương, c&oacute; khả năng đọc hiểu tốt<br />\r\n<strong>3. Quyền lợi:</strong><br />\r\n- L&agrave;m việc từ T2-T6 8h30-17h30<br />\r\n- Free coffe, gửi xe, party sinh nhật theo qu&yacute;<br />\r\n- M&ocirc;i trường l&agrave;m việc th&acirc;n thiện, trẻ nhiệt huyết<br />\r\n- Hưởng lương theo năng lực, review lương h&agrave;ng năm</p>\r\n\r\n<p><strong>Li&ecirc;n hệ:</strong><br />\r\n<span style="font-size:14px">Email: quangthao1606@gmail.com or hrmanagement@hbbsolution.com&nbsp;</span></p>\r\n', 1);

